import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { toast } from 'react-toastify';
import axiosInstance from '../utils/axios';

const Login = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axiosInstance.post('/auth/login', formData);
      localStorage.setItem('token', res.data.token);
      toast.success('Login successful!');

      // Redirect based on user role
      const { role } = res.data.user;
      switch (role) {
        case 'admin':
          navigate('/admin');
          break;
        case 'youth':
          navigate('/browse-tasks');
          break;
        case 'client':
          navigate('/dashboard');
          break;
        default:
          navigate('/dashboard');
      }
    } catch (err) {
      console.error('Login error:', err);
      toast.error(err.response?.data?.error || 'Login failed');
    }
  };

  return (
    <div className="container mx-auto p-8">
      <h2 className="text-2xl font-bold mb-4">Login</h2>
      <form onSubmit={handleSubmit} className="max-w-md mx-auto">
        <input type="email" name="email" placeholder="Email" onChange={handleChange} className="w-full p-2 mb-4 border" required />
        <input type="password" name="password" placeholder="Password" onChange={handleChange} className="w-full p-2 mb-4 border" required />
        <button type="submit" className="w-full bg-blue-600 text-white p-2 mb-4">Login</button>
      </form>
      <div className="text-center">
        <p className="mb-2">Don't have an account?</p>
        <Link to="/register" className="bg-green-600 text-white px-4 py-2 rounded">Create Account</Link>
      </div>
    </div>
  );
};

export default Login;
