import React, { useEffect, useState } from 'react';
import axios from 'axios';

const BrowseTasks = () => {
  const [tasks, setTasks] = useState([]);

  useEffect(() => {
    const fetchTasks = async () => {
      const token = localStorage.getItem('token');
      if (token) {
        const config = { headers: { Authorization: `Bearer ${token}` } };
        const res = await axios.get('http://localhost:5000/api/tasks', config);
        setTasks(res.data);
      }
    };
    fetchTasks();
  }, []);

  const acceptTask = async (id) => {
    const token = localStorage.getItem('token');
    const config = { headers: { Authorization: `Bearer ${token}` } };
    await axios.patch(`http://localhost:5000/api/tasks/${id}/accept`, {}, config);
    window.location.reload();
  };

  return (
    <div className="container mx-auto p-8">
      <h2 className="text-2xl font-bold mb-4">Browse Tasks</h2>
      {tasks.filter(t => t.status === 'open').map(task => (
        <div key={task._id} className="border p-4 mb-4">
          <h3 className="text-xl font-bold">{task.title}</h3>
          <p>{task.description}</p>
          <p>Location: {task.location}</p>
          <p>Price: KSh {task.price}</p>
          <button className="bg-green-600 text-white p-2" onClick={() => acceptTask(task._id)}>Accept Task</button>
        </div>
      ))}
    </div>
  );
};

export default BrowseTasks;
