import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Dashboard = () => {
  const [user, setUser] = useState(null);
  const [tasks, setTasks] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const token = localStorage.getItem('token');
        if (token) {
          const config = { headers: { Authorization: `Bearer ${token}` } };
          const userRes = await axios.get('http://localhost:5000/api/auth/me', config);
          setUser(userRes.data);
          const tasksRes = await axios.get('http://localhost:5000/api/tasks', config);
          setTasks(tasksRes.data);
        }
      } catch (err) {
        console.error('Failed to fetch dashboard data', err);
        if (err.response && (err.response.status === 401 || err.response.status === 403)) {
          // token invalid or not authorized — clear token and redirect to login
          localStorage.removeItem('token');
          window.location.href = '/login';
        }
      }
    };
    fetchData();
  }, []);

  if (!user) return <div>Loading...</div>;

  return (
    <div className="container mx-auto p-8">
      <h2 className="text-2xl font-bold mb-4">Dashboard</h2>
      <p>Welcome, {user.name} ({user.role})</p>
      {user.role === 'client' && (
        <div>
          <h3 className="text-xl font-bold mb-2">Your Tasks</h3>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
            {tasks.filter(t => t.client._id === user._id).map(task => (
              <div key={task._id} className="bg-white shadow-lg rounded-lg p-6 border">
                <h4 className="text-xl font-bold mb-2">{task.title}</h4>
                <p className="text-gray-700 mb-2">{task.description}</p>
                <p className="text-sm text-gray-500 mb-1"><strong>Location:</strong> {task.location}</p>
                <p className="text-sm text-gray-500 mb-1"><strong>Price:</strong> KSh {task.price}</p>
                <p className="text-sm text-gray-500 mb-1"><strong>Status:</strong> {task.status}</p>
                <p className="text-sm text-gray-500 mb-4"><strong>Skills:</strong> {task.skills.join(', ')}</p>
                {task.youth && <p className="text-sm text-gray-500"><strong>Accepted by:</strong> {task.youth.name}</p>}
              </div>
            ))}
          </div>
        </div>
      )}
      {user.role === 'youth' && (
        <div>
          <h3 className="text-xl font-bold mb-2">Available Tasks</h3>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
            {tasks.filter(t => t.status === 'open').map(task => (
              <div key={task._id} className="bg-white shadow-lg rounded-lg p-6 border">
                <h4 className="text-xl font-bold mb-2">{task.title}</h4>
                <p className="text-gray-700 mb-2">{task.description}</p>
                <p className="text-sm text-gray-500 mb-1"><strong>Location:</strong> {task.location}</p>
                <p className="text-sm text-gray-500 mb-1"><strong>Price:</strong> KSh {task.price}</p>
                <p className="text-sm text-gray-500 mb-4"><strong>Skills:</strong> {task.skills.join(', ')}</p>
                <button className="bg-green-600 hover:bg-green-700 text-white font-bold py-2 px-4 rounded" onClick={() => acceptTask(task._id)}>Accept Task</button>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );

  const acceptTask = async (id) => {
    const token = localStorage.getItem('token');
    const config = { headers: { Authorization: `Bearer ${token}` } };
    await axios.patch(`http://localhost:5000/api/tasks/${id}/accept`, {}, config);
    window.location.reload();
  };
};

export default Dashboard;
