import React, { useState } from 'react';
import axios from 'axios';

const PostTask = () => {
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    price: '',
    location: '',
    skills: ''
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    const config = { headers: { Authorization: `Bearer ${token}` } };
    try {
      await axios.post('http://localhost:5000/api/tasks', {
        ...formData,
        skills: formData.skills.split(',').map(s => s.trim())
      }, config);
      alert('Task posted successfully!');
    } catch (err) {
      alert('Failed to post task');
    }
  };

  return (
    <div className="container mx-auto p-8">
      <h2 className="text-2xl font-bold mb-4">Post a Task</h2>
      <form onSubmit={handleSubmit} className="max-w-md mx-auto">
        <input type="text" name="title" placeholder="Title" onChange={handleChange} className="w-full p-2 mb-4 border" required />
        <textarea name="description" placeholder="Description" onChange={handleChange} className="w-full p-2 mb-4 border" required />
        <input type="number" name="price" placeholder="Price (KSh)" onChange={handleChange} className="w-full p-2 mb-4 border" required />
        <input type="text" name="location" placeholder="Location" onChange={handleChange} className="w-full p-2 mb-4 border" required />
        <input type="text" name="skills" placeholder="Required Skills (comma separated)" onChange={handleChange} className="w-full p-2 mb-4 border" />
        <button type="submit" className="w-full bg-blue-600 text-white p-2">Post Task</button>
      </form>
    </div>
  );
};

export default PostTask;
