import React, { useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { supabase } from "../utils/supabase"; // adjust path if needed
import axios from "axios";

const AuthCallback = () => {
  const navigate = useNavigate();

  useEffect(() => {
    const verifyEmail = async () => {
      try {
        // Exchange the email verification token with Supabase
        const { data, error } = await supabase.auth.getSession();

        if (error || !data?.session) {
          console.error("Supabase session error:", error);
          navigate("/login?error=verification_failed");
          return;
        }

        const user = data.session.user;
        console.log("Verified Supabase user:", user);

        // Create MongoDB user profile
        await axios.post(
          `${process.env.REACT_APP_API_URL}/api/auth/createProfile`,
          {
            supabase_id: user.id,
            email: user.email,
            name: user.user_metadata?.name,
            role: user.user_metadata?.role,
            location: user.user_metadata?.location,
            skills: user.user_metadata?.skills,
            phone: user.user_metadata?.phone
          }
        );

        navigate("/login?verified=true");
      } catch (err) {
        console.error("Verification error:", err);
        navigate("/login?error=verification_failed");
      }
    };

    verifyEmail();
  }, [navigate]);

  return (
    <div className="flex items-center justify-center h-screen">
      <p className="text-lg">Verifying your email, please wait...</p>
    </div>
  );
};

export default AuthCallback;
